using System;

namespace NHibernate.Property.Bulk
{
	/// <summary>
	/// Base interface for the object generated by <see cref="NHibernate.Persister.GetSetHelperFactory"/>
	/// </summary>
	public interface IGetSetHelper
	{
		/// <summary>
		/// Set all the property of the object
		/// </summary>
		/// <param name="target">The object to load</param>
		/// <param name="values">The properties values</param>
		void SetPropertyValues( object target, object[] values );

		/// <summary>
		/// Get all the property of the object
		/// </summary>
		/// <param name="target">The object to save</param>
		/// <returns>The properties values</returns>
		object[] GetPropertyValues( object target );
	}
}
